#define pi 3.141592653589793
#define piby2 1.57079632679

float sketch_sin(float x){
    while (x < -pi){x += 2*pi;}
    while (x > pi){x -= 2*pi;}
    if (x < -piby2) return -first_quater_of_sin[127 - (int) (128*(- x/piby2 - 1))];
    else if(x < 0) return -first_quater_of_sin[(int) (128*(-x/piby2))];
    else if(x<piby2) return first_quater_of_sin[(int) (128*(x/piby2))];
    return first_quater_of_sin[127 - (int) (128*(x/piby2 - 1))];
}

float sketch_cos(float x){
    return sketch_sin(x + piby2);
}

float sketch_atan(float x){
    int neg =1;
    if(x<0){
        x *= -1;
        neg = -1;
    }
    x *= 4096;
    unsigned int*ptr=(unsigned int*)&x;
    float idx = (*ptr>>23)&0xff - 127;
    idx *= (8/15);
    return kinda_atan[(int) idx];
}